import React, { useState } from "react";
import CourseForm from "./CourseForm";

/** Arrow function component with props received */
const ManageCoursePage = (props) => {
  /**
   *  - declare state to hold course form data
   *  - initialise with an empty course
   * */
  const [course, setCourse] = useState({
    id: null,
    slug: "",
    title: "",
    authorId: "",
    categroy: "",
  });

  // declare change handler for form inputs
  function handleTitleChange() {
    // use ...spread operator to copy state for editing
    const updatedCourse = { ...course };
    // edit copied state
    updatedCourse.title = event.target.value;
  }

  return (
    <>
      <h2>Manage Course</h2>
      {/** Pass down to CourseForm child component via props
       *  - pass course state object
       *  - pass change handler */}
      <CourseForm course={course} onTitleChange={handleTitleChange} />
    </>
  );
};

export default ManageCoursePage;

// then create a route in App.js
