import React from "react";

// consume reusable TextInput component
import TextInput from "../shared/TextInput";

// import PropTypes
import PropTypes from "prop-types";

function CourseForm(props) {
  return (
    <form onSubmit={props.onSubmit}>
      {/** insert onSubmit on form tag rather than the
       *   submit button for better accessibility. User
       *   can click Save button or hit Enter to submit. */}
      <TextInput
        id="title"
        label="Title"
        onChange={props.onChange}
        name="title"
        value={props.course.title}
        error={props.errors.title}
      />
      <div className="form-group">
        <label htmlFor="author">Author</label>
        <div className="field">
          <select
            id="selectAuthor"
            name="author"
            onChange={props.onChange}
            /** value for select cannot be null so
             *  - set value to empty string if authorId
             *    is not selected. */
            value={props.course.authorId || ""}
            className="form-control"
          >
            <option value="" />
            <option value="1">Cory House</option>
            <option value="2">Scott Allen</option>
          </select>
        </div>
        {/** reuasable component SelectInput not setup for this form group
         *   as we did for TextInput so we need to display the error manually.
         */}
        {props.errors.authorId && (
          <div className="alert alert-danger">{props.errors.authorId}</div>
        )}
      </div>

      <TextInput
        id="category"
        label="Category"
        name="category"
        onChange={props.onChange}
        value={props.course.category}
        error={props.errors.category}
      />

      <input type="submit" value="Save" className="btn btn-primary" />
    </form>
  );
}

// declare PropTypes
CourseForm.propTypes = {
  course: PropTypes.object.isRequired,
  onSubmit: PropTypes.func.isRequired,
  onChange: PropTypes.func.isRequired,
  errors: PropTypes.object.isRequired,
};

export default CourseForm;
