import React from "react";

// consume reusable TextInput component
import TextInput from "../shared/TextInput";

function CourseForm(props) {
  return (
    <form onSubmit={props.onSubmit}>
      {/** insert onSubmit on form tag rather than the
       *   submit button for better accessibility. User
       *   can click Save button or hit Enter to submit. */}
      <TextInput
        id="title"
        label="Title"
        onChange={props.onChange}
        name="title"
        value={props.course.title}
        errors={props.errors.title}
      />
      <div className="form-group">
        <label htmlFor="author">Author</label>
        <div className="field">
          <select
            id="author"
            name="authorId"
            onChange={props.onChange}
            /** value for select cannot be null so
             *  - set value to empty string if authorId
             *    is not selected. */
            value={props.course.authorId || ""}
            className="form-control"
          >
            <option value="" />
            <option value="1">Cory House</option>
            <option value="2">Scott Allen</option>
          </select>
        </div>
      </div>

      <TextInput
        id="category"
        label="Category"
        name="category"
        onChange={props.onChange}
        value={props.course.category}
      />

      <input type="submit" value="Save" className="btn btn-primary" />
    </form>
  );
}

export default CourseForm;
